      {{-- Logo --}}
      <div class="flex items-center gap-4">
        <a 
          class="inline-block w-fit shrink-0" 
          href="{{ home_url('/') }}"
        >
          <img class="select-none" src="{{ Vite::asset('resources/images/logo.svg') }}">
